let rawObject = dynamic({jsonObject});
let _recordId = tostring(rawObject.recordId);

let tracesParsed = traces
| extend customDim = parse_json(customDimensions)
| where customDim has _recordId
| extend subscenario = tostring(customDim["powerplatform.analytics.subscenario"])
| extend recordId = iff(subscenario != "ConversationCreated" , tostring(customDim["powerplatform.analytics.resource.id"]), tostring(customDim["omnichannel.record.id"]))
| where isnotempty(recordId) and isnotnull(recordId)
| summarize by timestamp, recordId, subscenario, tostring(customDimensions)
| sort by timestamp asc
| project Timestamp = timestamp, RecordId = recordId, Subscenario = subscenario, ["Custom Dimensions"] = parse_json(customDimensions);

tracesParsed;
